    @height: 40px;

    .app-top-nav {
        &:before {
            /*占位元素*/
            display: block;
            content: " ";
            height: @height;
            width: 100%;
        }
    }

    .nav-bar {
        position: fixed;
        left: 0;
        top: 0;
        z-index: 99;
        width: 100%;
        height: @height;
        display: flex;
        justify-content: space-between;
        align-content: center;
        line-height: 1;
        background: #ffffff;
        border-bottom: 1px solid rgba(102, 102, 102, 0.6);

        span {
            padding: 12px 0;
            font-size: 16px;
        }

        .icon {
            color: transparent;
        }

        .icon img {
            height: 100%;
            width: auto;
        }

        .icon_back {
            background: url("data:image/png;base64,") no-repeat center / auto 60%;
        }

        .icon_reload {
            background: url("data:image/png;base64,") no-repeat center / auto 60%;
        }
    }
</style>